export default class CardIcon extends HTMLElement {
	connectedCallback () {
		console.error("This element is deprecated. Please use boom-item")
		this.innerHTML = `<div>
			<img src="${this.getAttribute("icon")}" 
				title="${this.getAttribute("title")}" 
				alt="${this.getAttribute("title")}"/>
			<h6>${this.getAttribute("title")}</h6>
		</div>`
	}
}