export default class CardProduct extends HTMLElement {
	connectedCallback () {
		this.innerHTML = `<div>
			<img class="banner" 
				src="${this.getAttribute("banner")}" 
				title="${this.getAttribute("title")}" 
				alt="${this.getAttribute("title")}"/>
			<div class="about">
				<h3>${this.getAttribute("title")}</h3>
				<span>${this.getAttribute("tags")}</span>
				<p>${this.getAttribute("description")}</p>
			</div>
		</div>`
	}
}