export default class CardSetting extends HTMLElement {
	connectedCallback () {
		this.innerHTML = `<div>
			<div class="text">
				<h4>${this.getAttribute("title")}</h4>
				<p>${this.getAttribute("description")}</p>
			</div>
			<input-switch 
				key="${this.getAttribute('key')}" 
				checked="${cookies.get(this.getAttribute('key'))}">
			</input-switch>
		</div>`
	}
}