export default class CardSimple extends HTMLElement {
	constructor() { super() }
	connectedCallback () {
		let html = ""
		if (this.getAttribute("google") == undefined) {
			html = `<img src="${this.getAttribute("icon")}" `
			if (this.getAttribute("icon") != undefined) {
				html += `title="${this.getAttribute("title")}"`
			}
			html += "/>"
		} else {
			html = `<span class="material-symbols-outlined">
				${this.getAttribute("icon")}
			</span>`
		}
		this.innerHTML = html
	}
}