import { els } from "./general";

const REVEAL_OFFSET = 100;

export function initReveal(parent) {
	parent.addEventListener("scroll", () => reveal(parent));
	reveal();
}

export function reveal(parent = window) {
	els(".reveal").forEach((view, i) => {
		if(view.getBoundingClientRect().top < parent.innerHeight - REVEAL_OFFSET) {
			view.classList.add("revealed");
		}
	});
}


