export default class BoomItem extends HTMLElement {
	connectedCallback() {
		let html = `<div>`;
		html += fillInfo(attr(this, "gicon"), attr(this, "icon"), attr(this, "title"));
		html += `</div>`;
		this.innerHTML = html;
	}
}

function fillInfo(gicon, icon, title, html = "") {
	if(gicon) {
		html += `<span class="material-symbols-outlined">${gicon}</span>`;
	}
	if(icon) {
		html += `<img src="${icon}">`;
	}
	if(title) {
		html += `<h6>${title}</h6>`;
	}
	return html;
}

function attr(context, name) {
	return context.getAttribute(name);
}


