export default class InputSwitch extends HTMLElement {
	connectedCallback () {
		let html = ""
		const key = this.getAttribute("key")
		html = `<label 
			for="${key}_switch" 
			class="switch">
		<input type="checkbox" `
		if (cookies.get(key)) html += "checked "
		html += `name="${key}_switch" 
			value="${key}_switch" 
			id="${key}_switch"
			onchange="cookies.set('
				${key}', 
				event.currentTarget.checked
		)">
			<span class="slider"></span>
		</label>`
		this.innerHTML = html
	}
}