export default class BoomItem extends HTMLElement {
	connectedCallback() {
		let html = `<div>`
		if(this.getAttribute("google") == null) {
			html += `<img src="${this.getAttribute('icon')}"
				title="${this.getAttribute('title') ?? 'Item'}"
				alt="${this.getAttribute('title') ?? 'Item'}">`
		} else {
			html += `<span class="material-symbols-outlined">
				${this.getAttribute("google")}
			</span>`
		}
		if(this.getAttribute("title") != null) html += `<h6>${this.getAttribute('title')}</h6>`
		html += `</div>`
		this.innerHTML = html
	}
}