export function el(query, queryChild) {
	if(typeof query == "object") {
		return query.querySelector(queryChild);
	}
	return document.querySelector(query);
}

export function els(query, queryChild) {
	if(typeof query == "object") {
		return query.querySelectorAll(queryChild);
	}
	return document.querySelectorAll(query);
}

export function go(url) {
	window.open(url, "_blank");
}

export function getParam(name) {
	const params = new URL(location).searchParams;
	const result = params.getAll(name).toString();
	return result == "" ? undefined: result;
}

export const save = {
	set: (key, value) => localStorage(key, value),
	get: key => localStorage.getItem(key)
}


