import { els } from "./general";

export function initReveal(parent, revealOffset) {
	parent.addEventListener("scroll", () => reveal(parent, revealOffset));
	reveal();
}

export function reveal(parent = window, revealOffset = 250) {
	els(".reveal").forEach((view, i) => {
		if(view.getBoundingClientRect().top < (parent.innerHeight - revealOffset)) {
			view.classList.add("revealed");
		}
	});
}

export function revealNow(element) {
	element.classList.add("revealed");
}


